package tools;

/**
 * @author Marvin
 *
 */
public class Tasks {

	String taskName = "";
	int taskWorkloadLeft = 0;
	int taskSizeOnCoding = 0;
	int taskSizeOnDatabase = 0;
	int taskSizeOnDesign = 0;
	int taskSizeOnTesting = 0;
	int taskValue = 0;
	int workingAgentOne = 0;
	int workingAgentTwo = 0;
	int userStory = 0;
	String status = "";


	public String getStatus() {
		return status;
	}

	public void setStatus(String status) {
		this.status = status;
	}


	public String getTaskName() {
		return taskName;
	}

	public void setTaskName(String taskName) {
		this.taskName = taskName;
	}

	public int getTaskWorkloadLeft() {
		return taskWorkloadLeft;
	}

	public void setTaskWorkloadLeft(int taskWorkloadLeft) {
		this.taskWorkloadLeft = taskWorkloadLeft;
	}

	public int getTaskSizeOnCoding() {
		return taskSizeOnCoding;
	}

	public void setTaskSizeOnCoding(int taskSizeOnCoding) {
		this.taskSizeOnCoding = taskSizeOnCoding;
	}

	public int getTaskSizeOnDatabase() {
		return taskSizeOnDatabase;
	}

	public void setTaskSizeOnDatabase(int taskSizeOnDatabase) {
		this.taskSizeOnDatabase = taskSizeOnDatabase;
	}

	public int getTaskSizeOnDesign() {
		return taskSizeOnDesign;
	}

	public void setTaskSizeOnDesign(int taskSizeOnDesign) {
		this.taskSizeOnDesign = taskSizeOnDesign;
	}

	public int getTaskSizeOnTesting() {
		return taskSizeOnTesting;
	}

	public void setTaskSizeOnTesting(int taskSizeOnTesting) {
		this.taskSizeOnTesting = taskSizeOnTesting;
	}

	public int getTaskValue() {
		return taskValue;
	}

	public void setTaskValue(int taskValue) {
		this.taskValue = taskValue;
	}

	public int getWorkingAgentOne() {
		return workingAgentOne;
	}

	public void setWorkingAgentOne(int workingAgentOne) {
		this.workingAgentOne = workingAgentOne;
	}

	public int getWorkingAgentTwo() {
		return workingAgentTwo;
	}

	public void setWorkingAgentTwo(int workingAgentTwo) {
		this.workingAgentTwo = workingAgentTwo;
	}

	public int getUserStory() {
		return userStory;
	}

	public void setUserStory(int userStory) {
		this.userStory = userStory;
	}



}
